import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import "./question.scss"
import { addAnswers } from "../../action";

function Question() {
    // Local state to store the questions
    const [questions, setQuestions] = useState([]);
    const [answerQuestion,setAnswerQuestion] = useState({})
    // Get the selected topic from the Redux store (replace with your correct state structure)
    const selectedTopicId = useSelector(state => state.questionReducer);
    const users = useSelector(state => state.UsersReducer)
    const dispacth = useDispatch();
    useEffect(() => {
        // Assuming selectedTopicId is a promise, resolve it
        const fetchQuestions = async () => {
            const resolvedQuestions = await selectedTopicId;
            setQuestions(resolvedQuestions); // Store the resolved data in local state
        };
        
        fetchQuestions();
    }, [selectedTopicId]);
    const handleTick = (questionId, answerIndex) => {
        setAnswerQuestion(prevState => ({
            ...prevState,
            [questionId]: answerIndex
        }));
    }
    const handleSumit = (e) => {
        e.preventDefault();
        if (!users) {
            console.error("User not logged in");
            return;
        }
        console.log("Submitted answers:", answerQuestion);
        const topics = questions[0].topics; // Assuming all questions have the same topic
        dispatch(addAnswers(answerQuestion, topics, users.id));
    }
    return (
        <>
           <form onSubmit={handleSumit}>
           <h1>Bài Quiz chủ đề: HTML5</h1>
            {
                questions.map(item => (
                    <div key={item.id}>
                        <h4>{item.id}.{item.question}</h4>
                             
                           {item.answer.map((ans, index) => (
                            <>
                             <div className="answer">
                             <input onClick={() => handleTick(item.id,index)}   checked={answerQuestion[item.id] === index} type="radio" />
                             <label  key={index}>{ans}</label>
                             </div>
                            </>
                            ))}
                    </div>
                ))
            }
            <input type="submit" value="Nộp bài " />
           </form>
        </>
    );
}

export default Question;
