import { createRegister, LoginAPI } from "../services/LoginServices";

const Users = async (state = [], action) => {
    console.log(state, action);

    switch (action.type) {
        case "CREATE_REGISTER":
            const result = await createRegister(action.info);
        case "LOGIN":
            try {
                const users = await LoginAPI();
                const user = users.find(item => item.email === action.email && item.password === action.password);

                if(user){
                    Swal.fire({
                        title: "Đăng nhập thất bại!",
                        text: "Thông tin đăng nhập không đúng.",
                        icon: "error",
                        confirmButtonText: "OK"
                    });
                    navigate("/home");
                }else {
                    Swal.fire({
                        title: "Đăng nhập thất bại!",
                        text: "Thông tin đăng nhập không đúng.",
                        icon: "error",
                        confirmButtonText: "OK"
                    });
                }
            } catch (error) {
                console.error("Error during login:", error);
                Swal.fire({
                    title: "Đăng nhập thất bại!",
                    text: "Đã xảy ra lỗi. Vui lòng thử lại.",
                    icon: "error",
                    confirmButtonText: "OK"
                });
                
            }
        default:
            return state;
    }

}
export default Users;
