import { useState } from "react"
import "./register.scss"
import { useDispatch } from "react-redux";
import { register } from "../../action";
import Swal from 'sweetalert2/dist/sweetalert2.js'
import 'sweetalert2/src/sweetalert2.scss'

function Register() {
    const [data, setData] = useState({});
    const dispatch = useDispatch();
    const handleOnchange = (e) => {
        const value = e.target.value;
        const name = e.target.name;

        setData({
            ...data,
            [name]: value
        });
        console.log(data)
    }
    const customStyles = {
        content: {
          top: '50%',
          left: '50%',
          right: 'auto',
          bottom: 'auto',
          marginRight: '-50%',
          transform: 'translate(-50%, -50%)',
        },
      };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const rand = () => {
            return Math.random().toString(36).substr(2);
        };

        const token = () => {
            return rand() + rand();
        };

        const createRegister = { ...data, token: token() };

        dispatch(register(createRegister));
        const result = await createRegister(createRegister);
        if(result){
            setShowModal(false);
            onReload();
            Swal.fire({
              icon: "success",
              title: "cập nhật sản phảm thành công ",
              showConfirmButton: false,
              timer: 1500
            })
          }


    }
    return (
        <>
            <form onSubmit={handleSubmit} >
                <div className="register">
                    <h2 className="register__title">Register Account</h2>
                    <div>
                        <input type="text" name="title" placeholder="FullName" onChange={handleOnchange} required />
                    </div>
                    <div>
                        <input type="email" name="email" placeholder="Email" onChange={handleOnchange} required />
                    </div>
                    <div>
                        <input type="text" name="password" placeholder="Password" onChange={handleOnchange} required />
                    </div>
                    <div className="register__button">
                        <input type="submit" value="Register" />
                    </div>
                </div>
            </form>
        </>
    )

}
export default Register
