import { useRef, useState } from "react"
import "./register.scss"
import { useDispatch } from "react-redux";
import { register } from "../../action";
import Swal from "sweetalert2"; // Import SweetAlert2
import {useNavigate } from "react-router-dom";


function Register() {
    const [data, setData] = useState({});
    const dispatch = useDispatch();
    const nameRef = useRef();
    const emailRef = useRef();
    const passRef = useRef();
    const navigate = useNavigate();
    const handleOnchange = (e) => {
        const value = e.target.value;
        const name = e.target.name;

        setData({
            ...data,
            [name]: value
        });
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        nameRef.current.value="";
        emailRef.current.value="";
        passRef.current.value="";
        dispatch(register({...data}));
        Swal.fire({
            title: "Đăng ký thành công!",
            text: "Chào mừng bạn đến với chúng tôi!",
            icon: "success",
            confirmButtonText: "OK"
        }).then(() => {
            // Điều hướng đến trang đăng nhập
            navigate("/login"),{state:{email:data.email}};
        });


    }
    return (
        <>
            <form onSubmit={handleSubmit} >
                <div className="register">
                    <h2 className="register__title">Register Account</h2>
                    <div>
                        <input ref={nameRef}  type="text" name="fullName" placeholder="FullName" onChange={handleOnchange} required />
                    </div>
                    <div>
                        <input ref={emailRef}  type="email" name="email" placeholder="Email" onChange={handleOnchange} required />
                    </div>
                    <div>
                        <input ref={passRef}   type="text" name="password" placeholder="Password" onChange={handleOnchange} required />
                    </div>
                    <div className="register__button">
                        
                        <input type="submit" value="Register" />
                    </div>
                </div>
            </form>
        </>
    )

}
export default Register
