import { useEffect, useState } from "react"
import { useDispatch } from "react-redux"
import { topicApi } from "../../services/ListQuestion";
import "./topic.scss"
import { addToDo } from "../../action";
import { Link, NavLink, Outlet } from "react-router-dom"

function Topic() {
    const [data, setData] = useState([]);
    const dispatch = useDispatch();
    useEffect(() => {
        const fecthApi = async () => {
            const result = await topicApi();
            setData(result);
        }
        fecthApi();
    }, []);
    const handleDo = (id) => {
        dispatch(addToDo(id))
    }
    return (
        <>
            <div className="container">
                <h3>Danh sách chủ đề ôn luyện </h3>
                <table className="table">
                    <thead className="table__head">
                        <tr >
                            <th>ID</th>
                            <th >Tên chủ đề </th>
                        </tr>
                    </thead>
                    <tbody >
                        {data.map(item => (
                            <>
                                <tr className="table__body" key={item.id}>
                                    <td >{item.id}</td>
                                    <td>{item.name}</td>
                                    <td>
                                        <Link
                                            to="question"
                                            className="btn"
                                            onClick={() => handleDo(item.id)}>
                                            Làm Bài
                                        </Link>
                                    </td>
                                </tr>
                            </>
                        ))}
                    </tbody>
                </table>
            </div>
        </>
    )
}
export default Topic
