    const navigate =useNavigate();
        switch (action.type) {
        case "CREATE_REGISTER":
            const result = await createRegister(action.info);
        case "LOGIN":
            const users = await LoginAPI();
            const user = users.find(item => item.email === action.email && item.password === action.password);
        if(user){
            Swal.fire({
                title: "Đăng Nhập thành công!",
                text: "Chào mừng bạn đến với chúng tôi!",
                icon: "success",
                confirmButtonText: "OK"
            });
            return {
                ...state,
               
            };

