import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getTopic } from "../../services/topicServies";
import { getListQuestion } from "../../services/questionSevies";
import { getCookie } from "../../helpers/cookie";

function Quiz() {
    const params = useParams();
    const [dataTopic, setDataTopic] = useState();
    const [dataQuestion, setDataQuestion] = useState([]);
    const userId = getCookie("id");
    console.log(userId);

    useEffect(() => {
        const fetchApi = async () => {
            const topics = await getTopic(params.id);
            setDataTopic(topics);
        };
        fetchApi();
    }, [params.id]); // Thêm params.id vào dependency array

    useEffect(() => {
        const fetchApi = async () => {
            const question = await getListQuestion(params.id);
            console.log(question);
            setDataQuestion(question);
        };
        fetchApi();
    }, ); // Thêm params.id vào dependency array

    const handleSubmit = (e) => {
        e.preventDefault();
        let selectAnswer = [];

        for (let i = 0; i < e.target.elements.length; i++) {
            if (e.target.elements[i].type === "radio" && e.target.elements[i].checked) {
                const name = e.target.elements[i].name;
                const value = e.target.elements[i].value;
                selectAnswer.push({
                    questionId: parseInt(name),
                    answers: parseInt(value)
                });
            }
        }
        console.log(selectAnswer);

        let options = {
            userId: userId,
            topicsId: parseInt(params.id),
            answers: selectAnswer
        };
        console.log(options);
    };

    return (
        <>
            <h1>Bài Quiz chủ đề: {dataTopic && dataTopic.name}</h1>
            <form onSubmit={handleSubmit}>
                {dataQuestion && dataQuestion.map((item, index) => (
                    <div key={item.id}>
                        <p>Câu {index + 1}: {item.question}</p>
                        {item.answer.map((itemAns, indexAns) => (
                            <div key={indexAns}>
                                <input
                                    type="radio"
                                    name={item.id}
                                    id={`quiz${item.id}${indexAns}`}
                                    value={indexAns}
                                />
                                <label htmlFor={`quiz${item.id}${indexAns}`}> {itemAns}</label>
                            </div>
                        ))}
                    </div>
                ))}
                <button type="submit">Nộp bài</button>
            </form>
        </>
    );
}

export default Quiz;
