import LayoutDefault from "../layout/LayoutDefault";
import Introduce from "../pages/introduce";
import Login from "../pages/login";
import Home from "../pages/Home";
import Register from "../pages/regeister"
import LayoutHome from "../layout/LayoutHome";
import Topic from "../pages/Topic";
import Answers from "../pages/Answers";
import LogOut from "../pages/Logout";
import Question from "../pages/Question";
import LayoutQuestion from "../layout/LayOutQuestion";

export const routes = [
    {
        path: "/",
        element: <LayoutDefault />,
        children: [
            {
                path: "/",
                element: <Introduce />
            },
            {
                path: "login",  // Đường dẫn tương đối
                element: <Login />
            },
            {
                path: "register",  // Đường dẫn tương đối
                element: <Register />
            }
        ]
    },
      // Các route sử dụng LayoutHome
      {
        path: "home",
        element: <LayoutHome />,
        children: [
            {
                path: "home",
                element: <Home />
            },
            {
                path: "answers",
                element: <Answers/>
            },
            {
                path: "logout",
                element: <LogOut/>
            }
        ]
    },
    ,
            {
                path: "topic",
                element: <LayoutQuestion />, // Sử dụng LayoutQuestion cho các route con của topic
                children: [
                    {
                        path: "",
                        element: <Topic />  // Hiển thị Topic ở đây
                    },
                    {
                        path: "question",
                        element: <Question /> // Hiển thị Question khi đến đây
                    }
                ]
            },
];
