import { useRef, useState } from "react"
import "./login.scss"
import { useDispatch } from "react-redux";
import Swal from "sweetalert2"; // Import SweetAlert2
import { useLocation } from "react-router-dom";


function Login() {
    const [data, setData] = useState({});
    const dispatch = useDispatch();
    const emailRef = useRef();
    const passRef = useRef();
    const location = useLocation();
    const {state} = location;
    const email = state?.email||"";

    const handleSubmit = async (e) => {
        e.preventDefault();
        emailRef.current.value="";
        passRef.current.value="";
        Swal.fire({
            title: "Đăng ký thành công!",
            text: "Chào mừng bạn đến với chúng tôi!",
            icon: "success",
            confirmButtonText: "OK"
           
        })


    }
    return (
        <>
            <form onSubmit={handleSubmit} >
                <div className="login">
                    <h2 className="login__title">Login Account</h2>
                    <div>
                        <input ref={emailRef}  type="email" name="email" placeholder="Email" onChange={handleOnchange} required />
                    </div>
                    <div>
                        <input ref={passRef}   type="text" name="password" placeholder="Password" onChange={handleOnchange} required />
                    </div>
                    <div className="login__button">
                        <input type="submit" value="Login" />
                    </div>
                </div>
            </form>
        </>
    )

}
export default Login
