import { useRef, useState } from "react"
import "./register.scss"
import { useDispatch } from "react-redux";
import { register } from "../../action";
import Swal from "sweetalert2"; // Import SweetAlert2


function Register() {
    const [data, setData] = useState({ 
        fullName: "",
        email: "",
        password: ""});
    const dispatch = useDispatch();
    const inputRef = useRef();
    const handleOnchange = (e) => {
        const value = e.target.value;
        const name = e.target.name;

        setData({
            ...data,
            [name]: value
        });
        console.log(data)
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        const rand = () => {
            return Math.random().toString(36).substr(2);
        };

        const token = () => {
            return rand() + rand();
        };

        const tokenRegister = { ...data, token: token() };
        dispatch(register(tokenRegister));
        Swal.fire({
            title: "Đăng ký thành công!",
            text: "Chào mừng bạn đến với chúng tôi!",
            icon: "success",
            confirmButtonText: "OK"
           
        }).then(() => {
            // Clear input fields
            // Optionally, clear the state as well
            setData({
                fullName: "",
                email: "",
                password: ""
            });
        });


    }
    return (
        <>
            <form onSubmit={handleSubmit} >
                <div className="register">
                    <h2 className="register__title">Register Account</h2>
                    <div>
                        <input ref={inputRef}  type="text" name="fullName" placeholder="FullName" onChange={handleOnchange} required />
                    </div>
                    <div>
                        <input ref={inputRef}  type="email" name="email" placeholder="Email" onChange={handleOnchange} required />
                    </div>
                    <div>
                        <input ref={inputRef}   type="text" name="password" placeholder="Password" onChange={handleOnchange} required />
                    </div>
                    <div className="register__button">
                        <input type="submit" value="Register" />
                    </div>
                </div>
            </form>
        </>
    )

}
export default Register
