import { useEffect, useState } from "react"
import "./register.scss"
import { useDispatch } from "react-redux";
import { register } from "../../action";

function Register(){
    const [data,setData] = useState({});
    const dispatch = useDispatch();
    const handleOnchange = (e) => {
        const value = e.target.value;
        const name = e.target.name;
    
        setData({
          ...data,
          [name]: value
        });
        console.log(data)
      }

    const handleSubmit = async (e) => {
        e.preventDefault();
        dispatch(register(e));

     

    }
    return (
        <>
        <form onSubmit={handleSubmit} >
        <div className="register">
            <h2 className="register__title">Register Account</h2>
            <div>
            <input type="text" name="title" placeholder="FullName" onChange={handleOnchange} required />
            </div>
            <div>
            <input type="email" name="email" placeholder="Email" onChange={handleOnchange}  required />
            </div>
            <div>
            <input type="text" name="password" placeholder="Password" onChange={handleOnchange} required />
            </div>
            <div className="register__button"> 
            <input type="submit" value="Register" />
            </div>
        </div>
        </form>
        </>
    )

}
export default Register
