import { useSelector } from "react-redux";

function Question() {
    // Get the selected topic from the Redux store
    const selectedTopicId = useSelector(state => state.questionReducer); // Adjust state selector based on your reducer structure

    console.log(selectedTopicId);

    return (
        <>
        <h2>Bài Quiz chủ đề: HTML5</h2>
        {
           selectedTopicId.legth>0 ?(
            selectedTopicId.map(item => (
                <>
                <div key={item.id}>
                    <p>{item.question}</p>
                    <ul>
                        {item.answer.map((ans,index) => (
                            <li key={index}>{ans}</li>
                        ))}
                    </ul>
                </div>
                </>
            ))
           ): (<> </>)}
       
        </>
    );
}

export default Question;
