import { useEffect, useState } from "react"
import { topicApi } from "../../services/ListQuestion";
import "./topic.scss"

function Topic() {
    const [data,setData] = useState([]);
    useEffect(() => {
        const fecthApi = async () => {
            const result = await topicApi();
            setData(result);
        }
        fecthApi();
    },[]);
    return(
        <>
       <div className="container">
       <h3>Danh sách chủ đề ôn luyện </h3>
        <table className="table">
            <thead className="table__head">
                <tr >
                <th>ID</th>
                <th>Tên chủ đề </th>
                </tr>
            </thead>
            <tbody >
                {data.map(item => (
                    <>
                    <tr key={item.id} className="table__body">
                    <td >{item.id}</td>
                    <td>{item.name}</td>
                    <td><button>Làm Bài</button></td>
                    </tr>
                    </>
                ))}
            </tbody>
        </table>
       </div>
        </>
    )
}
export default Topic
