import Swal from "sweetalert2"; // Import SweetAlert2
import { createRegister, LoginAPI } from "../services/LoginServices";
const Users = async (state = [], action) => {
    console.log(state, action);
        switch (action.type) {
        case "CREATE_REGISTER":
            const result = await createRegister(action.info);
        case "LOGIN":
            const users = await LoginAPI();
            const user = users.find(item => item.email === action.email && item.password === action.password);
        if(user){
            Swal.fire({
                title: "Đăng Nhập thành công!",
                text: "Chào mừng bạn đến với chúng tôi!",
                icon: "success",
                confirmButtonText: "OK"
            });

        }else(
            console.log("sai")
        )
        default:
            return state;
    }

}
export default Users;
