import { useEffect, useState } from "react"
import {useDispatch} from "react-redux"
import { topicApi } from "../../services/ListQuestion";
import "./topic.scss"
import { addToDo } from "../../action";

function Topic() {
    const [data,setData] = useState([]);
    const dispatch = useDispatch();
    useEffect(() => {
        const fecthApi = async () => {
            const result = await topicApi();
            setData(result);
        }
        fecthApi();
    },[]);
    const handleDo =(id) => {
        dispatch(addToDo(id))

    }
    return(
        <>
       <div className="container">
       <h3>Danh sách chủ đề ôn luyện </h3>
        <table className="table">
            <thead className="table__head">
                <tr >
                <th>ID</th>
                <th rowspan={2}>Tên chủ đề </th>
                </tr>
            </thead>
            <tbody >
                {data.map(item => (
                    <>
                    <tr key={item.id} className="table__body">
                    <td >{item.id}</td>
                    <td>{item.name}</td>
                    <td><button onClick ={handleDo(item.id)}>Làm Bài</button></td>
                    </tr>
                    </>
                ))}
            </tbody>
        </table>
       </div>
        </>
    )
}
export default Topic
