import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import "./question.scss";
import { addAnswers } from "../../action";

function Question() {
    const [questions, setQuestions] = useState([]);
    const [answerQuestion, setAnswerQuestion] = useState({});
    
    // Get the questions and user data from Redux
    const selectedTopicId = useSelector(state => state.questionReducer); // Assuming this holds the questions or topic info
   const user = useSelector(state => state.UserReducer)
    const dispatch = useDispatch();

    useEffect(() => {
        const fetchQuestions = async () => {
            if (selectedTopicId) {
                const resolvedQuestions = Array.isArray(selectedTopicId) ? selectedTopicId : await selectedTopicId;
                setQuestions(resolvedQuestions); // Store resolved data in local state
            }
        };

        fetchQuestions();
    }, [selectedTopicId]);

    const handleTick = (questionId, answerIndex) => {
        setAnswerQuestion(prevState => ({
            ...prevState,
            [questionId]: answerIndex
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log("Submitted answers:", answerQuestion);
        const topics = questions[0]?.topics; // Assuming all questions have the same topic
        dispatch(addAnswers(answerQuestion, topics, user));
        
    };

    return (
        <form onSubmit={handleSubmit}>
            <h1>Bài Quiz chủ đề: HTML5</h1>
            {questions.map(item => (
                <div key={item.id}>
                    <h4>{item.id}. {item.question}</h4>
                    {item.answer.map((ans, index) => (
                        <div className="answer" key={index}>
                            <input
                                onClick={() => handleTick(item.id, index)}
                                checked={answerQuestion[item.id] === index}
                                type="radio"
                                id={`q${item.id}a${index}`}
                            />
                            <label htmlFor={`q${item.id}a${index}`}>{ans}</label>
                        </div>
                    ))}
                </div>
            ))}
            <input type="submit" value="Nộp bài" />
        </form>
    );
}

export default Question;
