import { createRegister, LoginAPI } from "../services/LoginServices";

const Users = async (state = [], action) => {
    console.log(state, action);

    switch (action.type) {
        case "CREATE_REGISTER":
            const result = await createRegister(action.info);
        case "LOGIN":
            const users = await LoginAPI();
            const user = users.find(item => item.email=== action.info.email && item.password === action.info.password)
            if (user) {
                // Nếu tìm thấy người dùng
                return {
                    ...state,
                    loggedInUser: user // Cập nhật state với thông tin người dùng đã đăng nhập
                };
            } else {
                // Nếu không tìm thấy người dùng
                return {
                    ...state,
                    loggedInUser: null // Hoặc bạn có thể để nó không thay đổi nếu muốn
                };
            }
        default:
            return state;
    }

}
export default Users;
